var container = document.querySelector("body"),
	main = document.querySelector(".section-classname");

function toggleSidebar() {
	isShowingSidebar() ? hideSidebar() : showSidebar()
}

function showSidebar() {
	container.classList.add("show-sidebar")
}

function hideSidebar() {
	container.classList.remove("show-sidebar")
}

function isShowingSidebar() {
	return container.classList.contains("show-sidebar")
}
document.querySelector(".hamburger").addEventListener("click", toggleSidebar, !1), container.addEventListener("click", function (e) {
	isShowingSidebar() && main.contains(e.target) && (e.preventDefault(), hideSidebar())
}, !0);
